                carlospiroca.n.(e), true, false, false);
                carlospiroca.n.(b, r.x, r.y, r.z, 0, true);
                local d = carlospiroca.f._norm(f) * 100;
                carlospiroca.n.(b, 3, d.x, d.y, d.z, 0, 0, 0, false, false, true, true, false, true);
            end
        end
    end)
