carlospiroca.f.opts_menc = function(identity,param,index,slider,sliderIndex,sld)
    local colorKey = carlospiroca.t.identity[identity].sldcolor.i
    local identity = carlospiroca.t.identity[identity].sldcolor.ident

    carlospiroca.t.identity[identity .. 'r'].sldcolor[colorKey] = sliderIndex
    carlospiroca.t.identity[identity .. 'rg'].sldcolor[colorKey] = sliderIndex
    carlospiroca.t.identity[identity .. 'rgb'].sldcolor[colorKey] = sliderIndex
end

carlospiroca.f.menubutton = function(tabla, key, title, x, y, menu)
    if carlospiroca.f.check(tabla, key, title, x, y) then
        carlospiroca.t.menu[menu].show = not carlospiroca.t.menu[menu].show
    end
    return false
end

carlospiroca.f.button = function(tabla, key, title, x, y)
    if carlospiroca.f.check(tabla, key, title, x, y) then
        return true
    end
    return false
end

carlospiroca.f.checkbox = function(tabla, key, title, x, y)
    if carlospiroca.f.check(tabla, key, title, x, y, 'checkbox') then
        return 'checkbox'
    end
    return false
end

carlospiroca.f.sliderheader = function(tabla, key, title, x, y)
    carlospiroca.f.drawtext(title, x, y, 4, 0.3, false, 255, 255, 255, 255)
end

carlospiroca.f.slider = function(tabla, key, title, x, y)
    if carlospiroca.f.check(tabla, key, title, x, y, 'slider') then
        return true
    end

    return false
end

carlospiroca.f.combo = function(tabla, key, title, x, y)
    if carlospiroca.f.check(tabla, key, title, x, y, 'combo') then
        return true
    end

    return false
end

