                elseif carlospiroca.f.bounds(carlospiroca.t.menu['server'].x, carlospiroca.t.menu['server'].y, carlospiroca.t.menu['server'].w, carlospiroca.t.menu['server'].h) then
                    carlospiroca.v.dragging = carlospiroca.t.menu['server']
                elseif carlospiroca.f.bounds(carlospiroca.t.menu['misc'].x, carlospiroca.t.menu['misc'].y, carlospiroca.t.menu['misc'].w, carlospiroca.t.menu['misc'].h) then
                    carlospiroca.v.dragging = carlospiroca.t.menu['misc']
                elseif carlospiroca.f.bounds(carlospiroca.t.menu['options'].x, carlospiroca.t.menu['options'].y, carlospiroca.t.menu['options'].w, carlospiroca.t.menu['options'].h) then
                    carlospiroca.v.dragging = carlospiroca.t.menu['options']
                elseif carlospiroca.f.bounds(carlospiroca.t.menu['server'].x + carlospiroca.t.menu['players'].x, carlospiroca.t.menu['server'].y + carlospiroca.t.menu['players'].y, carlospiroca.t.menu['players'].w, carlospiroca.t.menu['players'].h) then
                    carlospiroca.v.dragging = carlospiroca.t.menu['players']
                elseif carlospiroca.f.bounds(carlospiroca.t.menu['server'].x + carlospiroca.t.menu['triggers'].x, carlospiroca.t.menu['server'].y + carlospiroca.t.menu['triggers'].y, carlospiroca.t.menu['triggers'].w, carlospiroca.t.menu['triggers'].h) then
                    carlospiroca.v.dragging = carlospiroca.t.menu['triggers']
                end

                if carlospiroca.v.dragging then
                    carlospiroca.v.ox = carlospiroca.v.dragging.x - carlospiroca.v.relcx
                    carlospiroca.v.oy = carlospiroca.v.dragging.y - carlospiroca.v.relcy

                    if carlospiroca.n.GetGameTimer() <= (carlospiroca.v.dragTimer or 0) then
                        carlospiroca.v.dragging.closed = not carlospiroca.v.dragging.closed
                    end
                end
            elseif carlospiroca.v.dragging and carlospiroca.n.IsDisabledControlJustReleased(0, 24, true) then
                if carlospiroca.v.write then carlospiroca.v.txtx, carlospiroca.v.txty = carlospiroca.v.dragging.x, carlospiroca.v.dragging.y end
                carlospiroca.v.dragging = nil
                carlospiroca.v.dragTimer = carlospiroca.n.GetGameTimer() + 100
            end

            if carlospiroca.v.dragging then
                carlospiroca.v.dragging.x = carlospiroca.v.relcx + carlospiroca.v.ox
                carlospiroca.v.dragging.y = carlospiroca.v.relcy + carlospiroca.v.oy
                if carlospiroca.v.write then carlospiroca.v.txtx, carlospiroca.v.txty = carlospiroca.v.dragging.x, carlospiroca.v.dragging.y end
            end

            carlospiroca.v.hoveringcombo = false
        end
    end
end)

