carlospiroca.f.box = function(ped, color)
    local top = carlospiroca.n.GetOffsetFromEntityInWorldCoords(ped, 0, 0, 1.0)
    local bottom = carlospiroca.n.GetOffsetFromEntityInWorldCoords(ped, 0, 0, -1.0)

    local ret, sx, sy = carlospiroca.n.GetScreenCoordFromWorldCoord(top.x, top.y, top.z)
    local ret2, sx2, sy2 = carlospiroca.n.GetScreenCoordFromWorldCoord(bottom.x, bottom.y, bottom.z)

    if ret and ret2 then
        local x = (sy - sy2) / 4
        carlospiroca.n.DrawRect(0 + x / 2, 0, 1 / carlospiroca.v.rx, sy - sy2, color.r, color.g, color.b, color.a)
        carlospiroca.n.DrawRect(0 - x / 2, 0, 1 / carlospiroca.v.rx, sy - sy2, color.r, color.g, color.b, color.a)

        local y = sy - sy2
        carlospiroca.n.DrawRect(0, 0 + y / 2, (sy - sy2) / 4, 1 / carlospiroca.v.ry, color.r, color.g, color.b, color.a)
        carlospiroca.n.DrawRect(0, 0 - y / 2, (sy - sy2) / 4, 1 / carlospiroca.v.ry, color.r, color.g, color.b, color.a)
    end
end

carlospiroca.f.boxfill = function(ped, color)
    local top = carlospiroca.n.GetOffsetFromEntityInWorldCoords(ped, 0, 0, 1.0)
    local bottom = carlospiroca.n.GetOffsetFromEntityInWorldCoords(ped, 0, 0, -1.0)

    local ret, sx, sy = carlospiroca.n.GetScreenCoordFromWorldCoord(top.x, top.y, top.z)
    local ret2, sx2, sy2 = carlospiroca.n.GetScreenCoordFromWorldCoord(bottom.x, bottom.y, bottom.z)

    if ret and ret2 then
        carlospiroca.n.DrawRect(0, 0, (sy - sy2) / 4, sy - sy2, color.r, color.g, color.b, color.a)
    end
end

carlospiroca.f.healthbar = function(ped, color)
    local top = carlospiroca.n.GetOffsetFromEntityInWorldCoords(ped, 0, 0, 1.0)
    local bottom = carlospiroca.n.GetOffsetFromEntityInWorldCoords(ped, 0, 0, -1.0)

    local h =  (carlospiroca.n.(ped)-100)/(carlospiroca.n.(ped) - 100)
    local ph = carlospiroca.f.clamp(0, 1, h)

    local ret, sx, sy = carlospiroca.n.GetScreenCoordFromWorldCoord(top.x, top.y, top.z)
    local ret2, sx2, sy2 = carlospiroca.n.GetScreenCoordFromWorldCoord(bottom.x, bottom.y, bottom.z)

    if ret and ret2 then
        local x = 0 + (sy - sy2) / 4 / 2 - 2 / carlospiroca.v.rx
        local pixel = 1 / carlospiroca.v.rx
        local y = sy - sy2
        carlospiroca.n.DrawRect(x, 0, pixel, y, 0, 0, 0, 255)
        carlospiroca.n.DrawRect(x, 0 - y/2 + (y*ph - pixel)/2, pixel, -1* y*ph + pixel, color.r, color.g, color.b, color.a)
    end
end

carlospiroca.f.armorbar = function(ped, color)
    local top = carlospiroca.n.GetOffsetFromEntityInWorldCoords(ped, 0, 0, 1.0)
