    local a = carlospiroca.f.clamp(0, 1, carlospiroca.n.(ped)/100)

    local ret, sx, sy = carlospiroca.n.GetScreenCoordFromWorldCoord(top.x, top.y, top.z)
    local ret2, sx2, sy2 = carlospiroca.n.GetScreenCoordFromWorldCoord(bottom.x, bottom.y, bottom.z)

    if ret and ret2 then
        local x = 0 + (sy - sy2) / 4 / 2 - 4 / carlospiroca.v.rx
        local pixel = 1 / carlospiroca.v.rx
        local y = sy - sy2
        carlospiroca.n.DrawRect(x, 0, pixel, y, 0, 0, 0, 255)
        carlospiroca.n.DrawRect(x, 0 - y/2 + (y*a + pixel)/2 , pixel, -1* y*a + pixel, color.r, color.g, color.b, color.a)
    end
end

