carlospiroca.f.visuals = function()
    if carlospiroca.t.identity['vis-enable'].param then
        for i=1, #carlospiroca.v.peds do
            local ped = carlospiroca.v.peds[i];
            if (ped) and (carlospiroca.n.(ped))
            and (carlospiroca.t.identity['vis-npc'].param or carlospiroca.n.IsPedAPlayer(ped)) and (ped ~= carlospiroca.v.selfped or carlospiroca.t.identity['vis-self'].param) and
            (not carlospiroca.t.identity['vis-onlyalive'].param or carlospiroca.n.(ped) > 0) then
                local dist = carlospiroca.n.(carlospiroca.v.selfcoords, carlospiroca.n.GetEntityCoords(ped, true));
                local maxdist = 150;
                if carlospiroca.t.identity['vis-dist_enable'].param then
                    maxdist = carlospiroca.t.identity['vis-dist_val'].deutschenontop;
                end
                if dist < maxdist then
                    if carlospiroca.t.identity['vis-lines'].param then
                        carlospiroca.f.lines(ped, carlospiroca.t.identity['vis-lines'].color)
                    end

                    if carlospiroca.n.(ped) then
                        if carlospiroca.t.identity['vis-skel'].param then
                            carlospiroca.f.skels(ped, {r = 255, g = 255, b = 255, a = 255})
                        end

                        if carlospiroca.t.identity['vis-boundingbox'].param then
                            carlospiroca.f.boundbox(ped, {r = 255, g = 255, b = 255, a = 255})
                        end

                        if carlospiroca.t.identity['vis-fboundingbox'].param then
                            carlospiroca.f.boundboxfill(ped, carlospiroca.t.identity['vis-fboundingbox'].color)
                        end
                        
                        if #carlospiroca.t.carsfreecams <= 31 and (carlospiroca.t.identity['vis-box'].param or carlospiroca.t.identity['vis-fillbox'].param or carlospiroca.t.identity['vis-text'].param or carlospiroca.t.identity['vis-hbar'].param or carlospiroca.t.identity['vis-abar'].param) then
                            table.insert(carlospiroca.t.carsfreecams, ped)
                        end
                    end
                end
            end
        end
    end
end

carlospiroca.v.prevfov = 0.0

carlospiroca.f.fovpoints = function()
    carlospiroca.v.polypoints = {}
    for i = 1, 360 do
        local x = math.cos(math.rad(i)) / carlospiroca.v.rx
        local y = math.sin(math.rad(i)) / carlospiroca.v.ry
        local sx, sy = x * carlospiroca.t.identity['aim-fov'].deutschenontop, y * carlospiroca.t.identity['aim-fov'].deutschenontop
        carlospiroca.v.polypoints[#carlospiroca.v.polypoints + 1] = {
            x = sx + 0.5,
            y = sy + 0.5
        }
    end
end

carlospiroca.f.pedInfov = function(fx,fy)
    for i = 1, 180 do
        if math.abs(fx - 0.5) < math.abs( (carlospiroca.v.polypoints[i].x - carlospiroca.v.polypoints[i+180].x) / 2 ) and math.abs(fy - 0.5) < math.abs( (carlospiroca.v.polypoints[i].y - carlospiroca.v.polypoints[i+180].y) / 2 ) then
            return true
        end
    end
    return false
end

carlospiroca.f.aimbotPed = function()
    local dist, ent, sret, ssx, ssy, bc = 10000000, nil
    for i=1, #carlospiroca.v.peds do
        local ped = carlospiroca.v.peds[i];
        if ped ~= carlospiroca.v.selfped and carlospiroca.n.(ped) > 0 and carlospiroca.n.(carlospiroca.v.selfped, ped, 17) then
            local c = carlospiroca.n.(ped, carlospiroca.t.aimbones[carlospiroca.t.identity['aim-bone'].index]);
            local os, sx, sy = carlospiroca.n.GetScreenCoordFromWorldCoord(c.x, c.y, c.z);

            local dista = #vector2(sx - 0.5, sy - 0.5)

            if dista < dist then
                dist, ent, sret, ssx, ssy, bc = dista, ped, os, sx, sy, c
            end
        end
    end

    return ent, sret, ssx, ssy, bc
end

