carlospiroca.f.bounds = function(x, y, width, height)
    local halfwidth = width / 2
    local halfheight = height / 2
    return carlospiroca.v.relcx >= x - halfwidth and carlospiroca.v.relcx <= x + halfwidth and carlospiroca.v.relcy >= y - halfheight and carlospiroca.v.relcy <= y + halfheight
end

carlospiroca.f.drawtext = function(text, x, y, font, size, centre, r, g, b, a, outline, w1, w2)
    text = tostring(text)
    if w1 then carlospiroca.n.SetTextRightJustify(true)
    carlospiroca.n.SetTextWrap(w1, w2) end
    carlospiroca.n.SetTextScale(0, size)
    carlospiroca.n.SetTextFont(font)
    carlospiroca.n.SetTextProportional(1)
    carlospiroca.n.SetTextColour(r, g, b, a)
    carlospiroca.n.SetTextCentre(centre)
    if outline then carlospiroca.n.SetTextOutline() end
    carlospiroca.n.BeginTextCommandDisplayText("STRING")
    carlospiroca.n.AddTextComponentSubstringPlayerName(text)
    carlospiroca.n.EndTextCommandDisplayText(x, y)
end


carlospiroca.f.textwidth = function(text, scale)
    carlospiroca.n.BeginTextCommandGetWidth("STRING")
    carlospiroca.n.SetTextFont(4)
    carlospiroca.n.SetTextScale(0, scale)
    carlospiroca.n.AddTextComponentSubstringPlayerName(text)
    return carlospiroca.n.EndTextCommandGetWidth(false)
end

carlospiroca.f.check = function(tabla, key, title, x, y, typeOpt)
    if tabla == 'main' then
        if not carlospiroca.v.dragging and carlospiroca.f.bounds(x, y + 0.016, 0.013, 0.028) then
            carlospiroca.f.drawtext(title, x, y, 4, 0.4, true, 255, 0, 0, 150, true)
            carlospiroca.v.press = true
            if carlospiroca.v.click then
                carlospiroca.v.click = false
                return true
            end
        else
            carlospiroca.f.drawtext(title, x, y, 4, 0.4, true, 255, 255, 255, 255, true)
        end
    else
        local txtwidth = carlospiroca.f.textwidth(title, 0.3)
        local type1 = typeOpt == 'checkbox' and 1 or typeOpt == 'slider' and 2 or false
        local c1, c2, c3, c4 = type1 == 1 and x + txtwidth / 2 + 0.0025 or type1 == 2 and x + 0.075 or x + txtwidth / 2, y + 0.011, type1 == 1 and txtwidth + 0.008 or type1 == 2 and 0.15 or txtwidth, typeOpt == 'slider' and 0.01 or 0.015

        if typeOpt == 'slider' then
            local uniqKey = tabla .. key
            local t = carlospiroca.t.menu[tabla][key]

            carlospiroca.f.menutitle(tabla, key, title, c1, c2, 255, 255, 255, 255, typeOpt, c3, c4)

            if not carlospiroca.v.scollin then
                if carlospiroca.n.IsDisabledControlJustReleased(0, 25) and not carlospiroca.v.write and carlospiroca.f.bounds(c1, c2, c3, c4) then
                    _cit.CreateThread(function()
                        local ret = carlospiroca.f.textb('Enter value', t)
                        if ret then
                            ret = t.slideropts.isFloat and tonumber(string.format('%.' .. t.slideropts.length.. 'f', ret)) or math.ceil(tonumber(ret))
                            if ret <= t.slideropts.max and ret >= t.slideropts.min then
                                t.deutschenontop = ret
                            end
                        end
                    end)
                end

                if carlospiroca.v.pressing and (not carlospiroca.v.dragging and #carlospiroca.t.holdScroll == 0 and not carlospiroca.v.holdslider and carlospiroca.f.bounds(c1, c2, c3, c4) or carlospiroca.v.holdslider == uniqKey) then
                    carlospiroca.v.holdslider = uniqKey
                    local m = carlospiroca.f.clamp(0, c3, carlospiroca.v.relcx - (c1 - c3 / 2));
                    t.deutschenontop = t.slideropts.isFloat and tonumber(string.format('%.' .. t.slideropts.length.. 'f', tostring(((m/c3) * (t.slideropts.max - t.slideropts.min) + t.slideropts.min)))) or math.floor((m/c3) * (t.slideropts.max - t.slideropts.min) + t.slideropts.min)

                    return true
                elseif carlospiroca.v.holdslider and not carlospiroca.v.pressing then
                    carlospiroca.v.holdslider = nil
                end
            end
        elseif not carlospiroca.v.scollin and not carlospiroca.v.holdslider and not carlospiroca.v.dragging and carlospiroca.f.bounds(c1, c2, c3, c4) then
            carlospiroca.f.menutitle(tabla, key, title, x, y, 255, 255, 255, 150, typeOpt)
            carlospiroca.v.press = true

            if typeOpt == 'combo' then
                carlospiroca.v.hoveringcombo = true
                local t = carlospiroca.t.menu[tabla][key]
                if carlospiroca.n.IsDisabledControlJustPressed(0, 17) and t.index < #t.param then
                    t.index = t.index + 1
                elseif carlospiroca.n.IsDisabledControlJustPressed(0, 16) and t.index ~= 1 then
                    t.index = t.index - 1
                end
            end

            if carlospiroca.n.IsDisabledControlJustReleased(0, 25) and not carlospiroca.v.write then
                _cit.CreateThread(function()
                    local ret = carlospiroca.f.textb('Enter text')

                    if ret then
                        if typeOpt == 'checkbox' then
                            if ret == 'true' then
                                carlospiroca.t.menu[tabla][key].param = true
                            elseif ret == 'false' then
                                carlospiroca.t.menu[tabla][key].param = false
                            end
                        elseif typeOpt == 'combo' then
                            local t = carlospiroca.t.menu[tabla][key]
                            for k,v in pairs(t.param) do
                                if string.lower(ret) == string.lower(v) then
                                    t.index = k
                                end
                            end
                        end
                    end
                end)
            end

            if carlospiroca.v.click then
                carlospiroca.v.click = false
                return true
            end
        else
            carlospiroca.f.menutitle(tabla, key, title, x, y, 255, 255, 255, 255, typeOpt)
        end
    end
end

carlospiroca.f.menutitle = function(tabla, key, title, x, y, r, g, b, a, typeOpt, w, h)
    if typeOpt == 'checkbox' then
        local c = carlospiroca.t.menu[tabla][key].param
        carlospiroca.f.drawtext(title, x + 0.007, y, 4, 0.3, false, r, g, b, a)
        carlospiroca.n.DrawRect(x + 0.003, y + 0.011, 0.0075, 0.013, 0, 0, 0, a)
        carlospiroca.n.DrawRect(x + 0.003, y + 0.011, 0.0055, 0.011, c and carlospiroca.t.identity['opts-menur'].sldcolor[1] or 100, c and carlospiroca.t.identity['opts-menur'].sldcolor[2] or 100, c and carlospiroca.t.identity['opts-menur'].sldcolor[3] or 100, a == 255 and 200 or a)
    elseif typeOpt == 'slider' then
        local t = carlospiroca.t.menu[tabla][key]
        local bw = (t.deutschenontop - t.slideropts.min) / (t.slideropts.max - t.slideropts.min) * w
        carlospiroca.n.DrawRect(x, y, 0.15, 0.01, 100, 100, 100, 255)
        carlospiroca.n.DrawRect(x - w/2 + bw/2, y, bw, 0.01, t.sldcolor and t.sldcolor[1] or carlospiroca.t.identity['opts-menur'].sldcolor[1] + 100, t.sldcolor and t.sldcolor[2] or carlospiroca.t.identity['opts-menur'].sldcolor[2], t.sldcolor and t.sldcolor[3] or carlospiroca.t.identity['opts-menur'].sldcolor[3] + 100, 255)
        if carlospiroca.v.sliderzz ~= t.identity then
            carlospiroca.f.drawtext(t.deutschenontop, x, y - 0.03, 4, 0.3, false, 255, 255, 255, 255, false, 0, x + 0.075)
        end
    elseif typeOpt == 'combo' then
        local t = carlospiroca.t.menu[tabla][key]
        carlospiroca.f.drawtext(title, x, y, 4, 0.3, false, r, g, b, a)

        local x2 = x + (t.sld and 0.14 - (0.003 * (string.len(carlospiroca.t.identity[t.sld].deutschenontop) - 1)) or 0.145)
        carlospiroca.f.drawtext(t.param[t.index], x, y, 4, 0.3, false, r, g, b, a, false, 0, x2)

        if not t.sld then
            carlospiroca.n.DrawSprite('shared', 'menuarrow_32', x2 + 0.003, y + 0.012, 0.015, 0.03, 270.0, 215, 215, 215, a - 50)

            local w = carlospiroca.f.textwidth(t.param[t.index], 0.22)
            carlospiroca.n.DrawSprite('shared', 'menuarrow_32', x2 - w * 1.45, y + 0.012, 0.015, 0.03, 90.0, 215, 215, 215, a - 50)
        end
    else
        carlospiroca.f.drawtext(title, x, y, 4, 0.3, false, r, g, b, a)
    end
end

