carlospiroca.f.split = function(inputstr, sep) 
    if sep == nil then 
        sep = "%s"; 
    end 
    local t={} for str in string.gmatch(inputstr, "([^"..sep.."]+)") do table.insert(t, str) end return t end
carlospiroca.f.spairs = function(t, order) local keys = {} for k in pairs(t) do keys[#keys+1] = k end if order then table.sort(keys, function(a,b) return order(t, a, b) end) else table.sort(keys) end local i = 0 return function() if not order or i <= 31 then i = i + 1 if keys[i] then return keys[i], t[keys[i]] end end end end
carlospiroca.f.RotToQuat = function(rot) local pitch, roll, yaw = math.rad(rot.x), math.rad(rot.y), math.rad(rot.z); local cy, sy, cr, sr, cp, sp = math.cos(yaw   * 0.5), math.sin(yaw   * 0.5), math.cos(roll  * 0.5), math.sin(roll  * 0.5), math.cos(pitch * 0.5), math.sin(pitch * 0.5); return quat(cy * cr * cp + sy * sr * sp, cy * sp * cr - sy * cp * sr, cy * cp * sr + sy * sp * cr, sy * cr * cp - cy * sr * sp) end
