carlospiroca.f.RotationToDirection = function(rotation)local retz = math.rad(rotation.z)local retx = math.rad(rotation.x)local absx = math.abs(math.cos(retx))return vector3(-math.sin(retz) * absx, math.cos(retz) * absx, math.sin(retx)) end

